mapper.insert:
  	INSERT 
  		INTO employees
  		VALUES (
  			#{idx}
			, #{idx}
  			, #{emp_name}
  			, #{dept_cd}
  			, #{grade_cd}
  			, #{emp_tel}
  			, #{emp_dtel}
  			, CONCAT(#{emp_email1}, "@", #{emp_email2}) 
  			, #{emp_passwd}
  			, #{emp_post_no}
  			, CONCAT(#{emp_addr1}, ", ", #{emp_addr2})
  			, #{hire_date}
  			, #{work_cd}
  			, #{priv_cd}
  			, #{photo}
  		)
  	
		SELECT LAST_INSERT_ID();

mapper.update:
		
		
			SELECT CONCAT(#{dept_cd}, MID(#{hire_date}, 3, 2), LPAD(#{idx}, 3, '0'))
				FROM employees 
 				WHERE idx = #{idx}
		
		UPDATE employees
			SET emp_num = #{emp_num}
			WHERE idx = #{idx}

  		UPDATE employees
  			SET
  				emp_name = #{emp_name}
	  			, dept_cd = #{dept_cd}
	  			, grade_cd = #{grade_cd}
	  			, emp_tel = #{emp_tel}
	  			, emp_dtel = #{emp_dtel}
	  			, emp_email = CONCAT(#{emp_email1}, "@", #{emp_email2})
	  			, emp_post_no = #{emp_post_no}
	  			, emp_addr = CONCAT(#{emp_addr1}, ", ", #{emp_addr2})
	  			, hire_date = #{hire_date}
	  			, work_cd = #{work_cd}
	  			
	  			, priv_cd = #{priv_cd}
	  			
	  			
	  			, photo = #{photo}
	  			
	  		WHERE 
	  			emp_email = #{emp_email}

		
			SELECT CONCAT(#{dept_cd}, MID(#{hire_date}, 3, 2), LPAD(#{idx}, 3, '0'))
				FROM employees 
 				WHERE emp_email = #{emp_email}
		
		UPDATE employees
			SET emp_num = #{emp_num}
			WHERE emp_email = #{emp_email}

mapper.select:
SELECT COUNT(*)
	FROM employees
	WHERE emp_email = #{emp_email}
