mapper.update:
UPDATE stock
	SET 
		stock_qty = #{stock_qty}
	WHERE 
		stock_cd = #{stock_cd}

UPDATE stock
	SET 
		stock_qty = stock_qty + #{moving_qty}
	WHERE 
		stock_cd = #{moving_stock_cd}

mapper.select:
SELECT product_cd
	FROM product
	WHERE product_name = #{product_name}

SELECT emp_num
	FROM employees
	WHERE emp_email = #{sId}

SELECT wh_loc_in_area_cd
	FROM wh_location
	WHERE wh_loc_in_area = #{loc_in_area}

mapper.insert:
INSERT INTO stock (
		product_cd
		, wh_loc_in_area_cd
		, stock_qty)
	VALUES (
		#{product_cd}
		, #{wh_loc_in_area_cd}
		, #{moving_qty}
	)
