mapper.update:
UPDATE stock
	SET 
		stock_qty = #{stock_qty}
	WHERE 
		stock_cd = #{stock_cd}

UPDATE stock
	SET 
		stock_qty = stock_qty + #{moving_qty}
	WHERE 
		stock_cd = #{moving_stock_cd}

mapper.select:
SELECT product_cd
	FROM product
	WHERE product_name = #{product_name}

SELECT emp_num
	FROM employees
	WHERE emp_email = #{sId}
